<script>
    var couponLink = document.querySelector('.showcoupon');
    var couponForm = document.querySelector('.checkout_coupon');

    // Khởi tạo biến để theo dõi trạng thái hiển thị của form
    var formVisible = false;

    couponLink.addEventListener('click', function(event) {
        event.preventDefault();
        
        // Nếu form chưa hiển thị, thì hiển thị form và cập nhật biến formVisible
        if (!formVisible) {
            couponForm.style.display = 'block';
            formVisible = true;
        } 
        // Nếu form đã hiển thị, thì ẩn form và cập nhật biến formVisible
        else {
            couponForm.style.display = 'none';
            formVisible = false;
        }
    });
</script>

